/** * */package com.hfv.hopgrower.activity;import android.os.Bundle;import android.support.v4.app.FragmentActivity;import com.hfv.hopgrower.R;import com.hfv.hopgrower.screens.GrowLogRightMainFragment;/** * @author Robert B. Andrews *         RBA Internet Services, Inc. Strasburg, Virginia *         Copyright 2014  All Rights Reserved */public class GrowLogEntriesActivity extends FragmentActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.grow_log_entries);        if (savedInstanceState == null) {            Bundle arguments = new Bundle();            arguments.putString("string1", getIntent()                    .getStringExtra("string2"));            GrowLogRightMainFragment fragment = new GrowLogRightMainFragment();            fragment.setArguments(arguments);            getSupportFragmentManager().beginTransaction()                    .add(R.id.growlog, fragment).commit();        }    }}