/** * */package com.hfv.hopgrower.adapter;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ArrayAdapter;import android.widget.TextView;import com.hfv.hopgrower.R;import com.hfv.hopgrower.special.GrowLogRec;import java.util.ArrayList;/** * @author Robert B. Andrews *         RBA Internet Services, Inc. Strasburg, Virginia *         Copyright 2014  All Rights Reserved */public class MailListAdapter extends ArrayAdapter<GrowLogRec> {    private int selectedPos = -1;    public MailListAdapter(Context context, ArrayList<GrowLogRec> glRecs) {        super(context, 0, glRecs);    }    @Override    public View getView(int position, View convertView, ViewGroup parent) {        GrowLogRec theRec = getItem(position);        View row;        row = LayoutInflater.from(getContext()).inflate(R.layout.mail_spinner_view, parent, false);        TextView theId = (TextView) row.findViewById(R.id.varId);        TextView theData = (TextView) row.findViewById(R.id.varItem);							/*	glID.setText(theRec.glId);		date.setText(theRec.date);		category.setText(theRec.category);		item.setText(theRec.item);		description.setText(theRec.glDesc);		*/        if (selectedPos == position) {            row.setBackgroundColor(0xFFAAFFEE);        } else {            row.setBackgroundColor(0xFF10DA5C);        }        if (theRec.date.equals("No Records")) {            row.findViewById(R.id.glID).setVisibility(View.GONE);        }        return row;    }    public void setSelectedPosition(int pos) {        selectedPos = pos;        notifyDataSetChanged();    }}