/** * */package com.hfv.hopgrower.special;import com.hfv.hopgrower.sqlite.myhops.GrowLog;import java.text.DateFormat;/** * @author Robert B. Andrews *         RBA Internet Services, Inc. Strasburg, Virginia *         Copyright 2014  All Rights Reserved */public class GrowLogRec {    public String glId;    public String date;    public String category;    public String item;    public String glDesc;    public void setRec(GrowLog glObj) {        glId = Long.toString(glObj.getId());        date = DateFormat.getDateTimeInstance().format(glObj.getLogDate());        category = glObj.getCategory();        item = glObj.getItem();        glDesc = glObj.getDescription();    }}