/** * */package com.hfv.hopgrower.special;import android.content.Context;import android.view.Gravity;import android.widget.ImageView;import android.widget.LinearLayout;import android.widget.TextView;import android.widget.Toast;import com.hfv.hopgrower.R;/** * @author Robert B. Andrews *         RBA Internet Services, Inc. Strasburg, Virginia *         Copyright 2014  All Rights Reserved *///not intended to extend Toast class, but to return a created toast objectpublic class HopGrowerToast {    Context mCtx;    CharSequence ttext;    int imageInd;    public HopGrowerToast(Context context, CharSequence text, int imgInd) {        super();        mCtx = context;        ttext = text;        imageInd = imgInd;    }    public Toast getToast() {        //attributes 1 is text, 2 is image        LinearLayout layout = new LinearLayout(mCtx);        layout.setBackgroundResource(R.drawable.toast_border);        ImageView toastImg = getImageView(imageInd);        TextView tv = new TextView(mCtx);        int duration = Toast.LENGTH_SHORT;        int black = mCtx.getResources().getColor(R.color.black);        tv.setTextColor(black);        tv.setTextSize(25);        tv.setGravity(Gravity.CENTER_VERTICAL);        tv.setText(ttext);        layout.addView(toastImg);        layout.addView(tv);        Toast toast = new Toast(mCtx);        toast.setView(layout);        //0,0  x and y   x= horizontal, y is vertical        toast.setGravity(Gravity.CENTER_HORIZONTAL | Gravity.CENTER_VERTICAL, 0, 0);        toast.setDuration(duration);        return toast;    }    private ImageView getImageView(int imageInd) {        Integer[] Icons = new Integer[]{                R.drawable.hop_cone2, R.drawable.info, R.drawable.hop_cone,                R.drawable.kbase, R.drawable.tools2,                R.drawable.schedule, R.drawable.notes2,                R.drawable.settings, R.drawable.link, R.drawable.link                //image 8 is growlog, last image is link        };        ImageView img = new ImageView(mCtx);        // give the drawable resource for the ImageView        int drawable = Icons[imageInd];        img.setImageResource(drawable);        return img;    }}/*EXAMPLES// create a LinearLayout and ViewsLinearLayout  layout=new LinearLayout(this);layout.setBackgroundResource(R.color.LightOrange);TextView  tv=new TextView(this);// set the TextView properties like color, size etctv.setTextColor(Color.RED);tv.setTextSize(15);        tv.setGravity(Gravity.CENTER_VERTICAL);		toast.setGravity(Gravity.CENTER_HORIZONTAL|Gravity.CENTER_VERTICAL, 0, 0);					                                               0 = x and 0 = y// set the text you want to show in  Toasttv.setText("My Custom Toast at Bottom of Screen");  ImageView   img=new ImageView(this);// give the drawble resource for the ImageViewimg.setImageResource(R.drawable.myimage);// add both the Views TextView and ImageView in layoutlayout.addView(img);layout.addView(tv);Toast toast=new Toast(this); //context is object of Context write "this" if you are an Activity// Set The layout as Toast Viewtoast.setView(layout);       // Position you toast here toast position is 50 dp from bottom you can give any integral value    toast.setGravity(Gravity.BOTTOM, 0, 50); toast.show();*/