/** * */package com.hfv.hopgrower.special;import android.content.Context;import android.support.v4.view.ViewPager;import android.util.AttributeSet;import android.view.MotionEvent;/** * @author Robert B. Andrews *         RBA Internet Services, Inc. Strasburg, Virginia *         Copyright 2014  All Rights Reserved *///special class that allows the swipe to be disabled locking the current view on the screen//call setPagingEnabled(false) to lock screenpublic class MyHopsViewPager extends ViewPager {    /**     * @param context     * @param attrs     */    private boolean enabled;    public MyHopsViewPager(Context context, AttributeSet attrs) {        super(context, attrs);        this.enabled = true;    }    @Override    public boolean onTouchEvent(MotionEvent event) {        if (this.enabled) {            return super.onTouchEvent(event);        }        return false;    }    public boolean onInterceptTouchEvent(MotionEvent event) {        if (this.enabled) {            return super.onInterceptTouchEvent(event);        }        return false;    }    public void setPagingEnabled(boolean enabled) {        this.enabled = enabled;    }}